import React, { useState, useEffect } from 'react';
import { ResponsiveContainer, PieChart, Pie, Cell, Sector, Tooltip, Legend } from 'recharts';
import styled from 'styled-components';
import axios from 'axios';



const StyledChart1 = styled.div`
  height: 40%;
  width: 40%; 
  text-align: center; /* Aligns <a> inside of NavIcon div */
  background-color: #F5F5F5;
  margin: 10px 10px;
  padding: 12px 6px;
  border-radius: 5px;

`;
const Wrapper = styled.div`
  margin-top: 5em;
  margin-left: 7em;
  margin-right: 20em;
  margin-bottom: 5em;
`;
const Dashboard = () => {

  const [data2, setData] = useState([]);
  useEffect(() => {
    Promise.all([
      axios.get('/dashboard'),
    ]).then((all) => {
      setData(all[0].data);   
      //console.log("all",all);
      console.log("data", data2);
    });
  }, []);

 console.log("data",data2);
  // const data = [
  //   { name: 'Quality reviews in progress', value: 152 }, { name: 'Quality review completed', value: 300 },
  // ];

const data =[
      { name: "facebook", value: 120111},
      {name: "Instagram", value : 1547870},
   ]; 

const COLORS = ['#FFC107', '#4CAF50'];
const RADIAN = Math.PI / 180;
const renderCustomizedLabel = ({
  cx, cy, midAngle, innerRadius, outerRadius, percent, index,
}) => {
   const radius = innerRadius + (outerRadius - innerRadius) * 0.5;
  const x = cx + radius * Math.cos(-midAngle * RADIAN);
  const y = cy + radius * Math.sin(-midAngle * RADIAN);

  return (
    <text x={x} y={y} fill="white" textAnchor={x > cx ? 'start' : 'end'} dominantBaseline="central">
      {`${(percent * 100).toFixed(0)}%`}
    </text>
  );
};

return (

  <Wrapper>
  <div className ='App'>   
  <h2>Dashboard</h2>
  <StyledChart1>
  <h5> Quality reviews </h5>
  <PieChart width={400} height={400}>
          <Pie
          data={data}
          cx={200}
          cy={200}
          //labelLine={true}
          //label
          labelLine={false}
          label={renderCustomizedLabel}
          outerRadius={130}
          fill="#8884d8"
          dataKey="value"
          Legend
        >
          {
            data.map((entry, index) => <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />)
          }
        </Pie>
        <Tooltip/>
        <Legend/>
      </PieChart>

  </StyledChart1>

    </div>
  </Wrapper>
)

};



export default Dashboard;
