import React, { useState, useEffect } from 'react';
import { BarChart, XAxis, YAxis, CartesianGrid, Bar, ResponsiveContainer, PieChart, Pie, Cell, Sector, Tooltip, Legend } from 'recharts';
import styled from 'styled-components';
import axios from 'axios';



const StyledChart1 = styled.div`
  height: 40%;
  width: 40%; 
  text-align: center; /* Aligns <a> inside of NavIcon div */
  background-color: #F5F5F5;
  margin: 10px 10px;
  padding: 12px 6px;
  border-radius: 5px;

`;
const Wrapper = styled.div`
  margin-top: 5em;
  margin-left: 7em;
  margin-right: 20em;
  margin-bottom: 5em;
`;
const Dashboard = () => {

  const [data2, setData] = useState([]);
  useEffect(() => {
    Promise.all([
      axios.get('/dashboard'),
    ]).then((all) => {
      console.log("all[0]",all[0]);
      setData(all);   

    });
  }, []);
 console.log("data2",data2);


//  const [data2, setData] = useState([]);
//  useEffect(() => {
//    Promise.all([
//      axios.get('/dashboard'),
//    ]).then((all) => {
//      setData(all[0].data[0].count);   
//      console.log("all[0]",all);
//    });
//  }, []);
// console.log("data2",data2);


 // const data = [
  //   { name: 'Quality reviews in progress', value: 152 }, { name: 'Quality review completed', value: 300 },
  // ];

const data =[
      { name: "facebook", value: 5},
      {name: "Instagram", value : 6},
   ]; 
const COLORS = ['#FFC107', '#4CAF50'];
const RADIAN = Math.PI / 180;
const renderCustomizedLabel = ({
  cx, cy, midAngle, innerRadius, outerRadius, percent, index,
}) => {
   const radius = innerRadius + (outerRadius - innerRadius) * 0.5;
  const x = cx + radius * Math.cos(-midAngle * RADIAN);
  const y = cy + radius * Math.sin(-midAngle * RADIAN);

  return (
    <text x={x} y={y} fill="white" textAnchor={x > cx ? 'start' : 'end'} dominantBaseline="central">
      {`${(percent * 100).toFixed(0)}%`}
    </text>
  );
};

const data3 = [
  {
    name: 'Current project', current: 4000,
  },
  {
    name: 'Project completed', completed: 3000,
  },
  {
    name: 'Future Project', future: 2000,
  },

];
return (

  <Wrapper>
  <div className ='App'>   
  <h2>Dashboard</h2>
  <StyledChart1>
  <h5> Quality reviews </h5>
  <PieChart width={400} height={400}>
          <Pie
          data={data}
          cx={200}
          cy={200}
          //labelLine={true}
          //label
          labelLine={false}
          label={renderCustomizedLabel}
          outerRadius={130}
          fill="#8884d8"
          dataKey="value"
          Legend
        >
          {
            data.map((entry, index) => <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />)
          }
        </Pie>
        <Tooltip/>
        <Legend/>
  </PieChart>
  </StyledChart1>
  <StyledChart1>
  <BarChart
        width={500}
        height={300}
        data={data3}
        margin={{
          top: 5, right: 30, left: 20, bottom: 5,
        }}
      >
        <CartesianGrid strokeDasharray="3 3" />
        <XAxis dataKey="name" />
        <YAxis />
        <Tooltip />
        <Legend />
        <Bar dataKey="current" fill="#8884d8" />
        <Bar dataKey="completed" fill="#82ca9d" />
        <Bar dataKey="future" fill="#82ca9d" />
  </BarChart>
  </StyledChart1>

    </div>
  </Wrapper>
)

};



export default Dashboard;
